mapper.update:
update REVIEW_BOARD
set    REVIEW_READCOUNT = REVIEW_READCOUNT + 1
where  REVIEW_NUM = #{num}

	update REVIEW_BOARD
    set    REVIEW_SUBJECT = #{REVIEW_SUBJECT},
	       REVIEW_CONTENT = #{REVIEW_CONTENT}
    where  REVIEW_NUM = #{REVIEW_NUM}

mapper.select:
select * from REVIEW_BOARD
where    REVIEW_NUM = #{REVIEW_NUM} 
and      REVIEW_PASS = #{REVIEW_PASS}
